import { HfInference } from '@huggingface/inference'

const SystemPrompt = `You are a cooking assistant. Given a list of ingredients, generate a recipe using only them (or extras if necessary).  
Format the response in **Markdown**, using:  
- **<h2>** for section titles (e.g., "Ingredients" and "Instructions").  
- **Bullet points (-)** for ingredients.  
- **Numbered steps (1., 2., etc.)** for instructions.  
Ensure proper formatting as per instructions.`;

const hf = new HfInference("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")

export async function getRecipeFromMistral(ingredientsArr) {
    const ingredientsString = ingredientsArr.join(", ")
    try {
        const response = await hf.chatCompletion({
            model: "mistralai/Mixtral-8x7B-Instruct-v0.1",
            messages: [
                { role: "system", content: SystemPrompt },
                { role: "user", content: `I have the following ingredients ${ingredientsString}. Please give me a tasty recipe I can make with them!` },
            ],
            max_tokens: 1024,
        })
        return response.choices[0].message.content
    } catch (err) {
        console.error(err.message)
    }
}
